package java_package;

public class Practiceevenandoddloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  
		for(int a=20;a<=40;a++)
		{
			if(a%2 ==0)
			{	
				System.err.println("the number is even "+a);
				
			}
			else
			{
				System.out.println("the number is odd"+a);
		
				
			}
		}
	}

}
